package rasberry;

import java.util.*;

public class MapProgram {
  public static void main(String[] args) {
	  Map<String, String> newMap = new HashMap<>(); 
	  newMap.put("Java", "a compiler high level object oriented, platform independent language. "); 
	  newMap.put("Truth", "what you should say. ");
	  newMap.put("Work", "don't work for money, work to learn. ");
	  
	  System.out.println(newMap.get("Truth"));
	  System.out.println("");

	  newMap.remove("Truth"); 
	  for(String key: newMap.keySet()) {
		  System.out.println(key +" : "+ newMap.get(key));
	  }
	  System.out.println("");

	  if(newMap.remove("Work", "don't work for money, work to learn")) {
		  System.out.println("work removed");
	  }
	  else {
		  System.out.println("work nor removed, key/value pair not matched! ");
	  }
	  System.out.println("");

/* An important thing to remember is that for a particular key can only be one value and if you
 * try to insert more than once using the put more than once then the value get overwritten. 
 * 
 * 	  
 */
	  
  }
}
